import React from 'react';

class GoalForm extends React.Component {

  constructor(props){
    super(props);
    this.handleFormSubmission = this.handleFormSubmission.bind(this);
    this.handleChange = this.handleChange.bind(this);

    this.state = {
      name: '',
      description: ''
    }
  }


  handleFormSubmission = (e) => {
    e.preventDefault();
    console.log("Form submitted");
  }

  handleChange = (e) => {
    console.log(e.target.value);
  }

  render() {
    return (
      <div>
        <form onSubmit={this.handleFormSubmission}>
          <input type="text" value={this.state.name} placeholder="name" onChange={this.handleChange}></input>
          <input type="text" value={this.state.description} placeholder="description" onChange={this.handleChange}></input>
          <input type="submit"/>
        </form>
      </div>
    )
  }
}

export default GoalForm;
